mapper.update:
update product_info
set
productid = #{productid},
name = #{name},
model = #{model},
moldid = #{moldid}
where productid = #{productid}
and userid = #{userid}

mapper.delete:
delete from product_info
where productid = #{productid}
and userid = #{userid}

mapper.select:
select pi.productid, pi.name, pi.model, pi.moldid, mn.quantity from product_info pi inner join mold_number mn on mn.moldid = pi.moldid and mn.userid = #{userid}
pi.userid = #{userid}
and pi.productid like CONCAT('%', #{productid}, '%')
and pi.name like CONCAT('%', #{name}, '%')
and pi.model like CONCAT('%', #{model}, '%')
and pi.moldid like CONCAT('%', #{moldid}, '%')
and mn.quantity = #{quantity}

select count(*)
from product_info
where productid = #{productid}
and userid = #{userid}
