mapper.update:
update unit_info
set
unitid = #{unitid},
name = #{name},
person = #{person},
phone = #{phone},
address = #{address},
type = #{type}
where unitid = #{unitid}
and userid = #{userid}

mapper.delete:
delete from unit_info
where unitid = #{unitid}
and userid = #{userid}

mapper.select:
select count(*)
from unit_info
where unitid = #{unitid}
and userid = #{userid}

select count(*)
from unit_info
where unitid = #{unitid}
  and userid = #{userid}
